% Abstract Algebra: Groups, Rings, and Fields
\section*{Algebraic Structures}

\subsection{Group Theory}

\begin{definition}
A \textbf{group} $(G, \cdot)$ is a set $G$ with a binary operation $\cdot$ satisfying:
\begin{enumerate}
\item \textbf{Closure:} $a \cdot b \in G$ for all $a, b \in G$
\item \textbf{Associativity:} $(a \cdot b) \cdot c = a \cdot (b \cdot c)$
\item \textbf{Identity:} $\exists e \in G$ such that $a \cdot e = e \cdot a = a$ for all $a \in G$
\item \textbf{Inverse:} For each $a \in G$, $\exists a^{-1} \in G$ such that $a \cdot a^{-1} = a^{-1} \cdot a = e$
\end{enumerate}
\end{definition}

\textbf{Lagrange's theorem:}
If $G$ is a finite group and $H$ is a subgroup of $G$, then $|H|$ divides $|G|$.

\textbf{Cyclic groups:}
A group $G$ is \textbf{cyclic} if $G = \langle g \rangle = \{g^n : n \in \mathbb{Z}\}$ for some $g \in G$.

\subsection{Ring Theory}

\begin{definition}
A \textbf{ring} $(R, +, \cdot)$ is a set $R$ with two operations satisfying:
\begin{enumerate}
\item $(R, +)$ is an abelian group
\item Multiplication is associative
\item Distributive laws: $a(b + c) = ab + ac$ and $(a + b)c = ac + bc$
\end{enumerate}
\end{definition}

\textbf{Integral domain:}
A commutative ring with unity and no zero divisors.

\textbf{Principal ideal domain (PID):}
An integral domain where every ideal is generated by a single element.

\subsection{Field Theory}

\begin{definition}
A \textbf{field} is a commutative ring where every non-zero element has a multiplicative inverse.
\end{definition}

\textbf{Examples:}
\begin{itemize}
\item $\mathbb{Q}, \mathbb{R}, \mathbb{C}$ (infinite fields)
\item $\mathbb{Z}_p$ for prime $p$ (finite fields)
\item $\mathbb{F}_{p^n}$ (finite fields of order $p^n$)
\end{itemize}

\textbf{Field extensions:}
If $F \subseteq K$ are fields, then $K$ is a \textbf{field extension} of $F$.

\textbf{Degree of extension:} $[K : F] = \dim_F K$ (dimension as vector space)

\textbf{Fundamental theorem of algebra:}
$\mathbb{C}$ is algebraically closed: every non-constant polynomial in $\mathbb{C}[x]$ has a root in $\mathbb{C}$.
