\documentclass[11pt]{article}
\usepackage{amsmath,amssymb,amsthm}
\usepackage{geometry}
\geometry{margin=1in}
\theoremstyle{definition}
\newtheorem{theorem}{Theorem}
\newtheorem{definition}{Definition}
\newtheorem{example}{Example}
\title{Mathematical Foundations: A Sample Document}
\author{A. N. Researcher}
\date{\today}
\begin{document}
\maketitle
\section{Introduction}
This document demonstrates the conversion of a complete LaTeX document to HTML using Pandoc-WASM with MathJax rendering.
\section{Basic Mathematical Concepts}
\begin{definition}
A \textbf{group} $(G, \cdot)$ is a set $G$ together with a binary operation $\cdot$ satisfying:
\begin{enumerate}
\item \textbf{Closure:} $\forall a,b \in G$, $a \cdot b \in G$
\item \textbf{Associativity:} $\forall a,b,c \in G$, $(a \cdot b) \cdot c = a \cdot (b \cdot c)$
\item \textbf{Identity:} $\exists e \in G$ such that $\forall a \in G$, $e \cdot a = a \cdot e = a$
\item \textbf{Inverse:} $\forall a \in G$, $\exists a^{-1} \in G$ such that $a \cdot a^{-1} = a^{-1} \cdot a = e$
\end{enumerate}
\end{definition}
\begin{example}
The set of integers $\mathbb{Z}$ with addition forms a group:
\[e = 0, \quad a^{-1} = -a \text{ for any } a \in \mathbb{Z}\]
\end{example}
\section{Advanced Topics}
\subsection{Linear Algebra}
The characteristic polynomial of a matrix $A$ is:
\[p(\lambda) = \det(A - \lambda I)\]
Eigenvalues satisfy:
\[A\mathbf{v} = \lambda\mathbf{v}\]
\subsection{Analysis}
\begin{theorem}[Fundamental Theorem of Calculus]
If $f$ is continuous on $[a,b]$, then:
\[\frac{d}{dx}\int_a^x f(t)\,dt = f(x)\]
\end{theorem}
\section{Conclusion}
This demonstrates how LaTeX documents with mathematical content can be successfully converted to HTML whilst preserving both structure and mathematical notation through MathJax.
\end{document}

