% Differential Equations: Methods and Applications
\section*{Ordinary Differential Equations}

\textbf{First-order linear ODE:}
\[\frac{dy}{dx} + P(x)y = Q(x)\]

Solution using integrating factor $\mu(x) = e^{\int P(x) dx}$:
\[y = \frac{1}{\mu(x)}\left[\int \mu(x)Q(x) dx + C\right]\]

\textbf{Separable equations:}
\[\frac{dy}{dx} = f(x)g(y)\]

Separate variables:
\[\frac{dy}{g(y)} = f(x) dx\]

Integrate both sides:
\[\int \frac{dy}{g(y)} = \int f(x) dx + C\]

\textbf{Second-order linear homogeneous:}
\[ay'' + by' + cy = 0\]

Characteristic equation: $ar^2 + br + c = 0$

\textbf{Case 1:} Two distinct real roots $r_1, r_2$:
\[y = c_1 e^{r_1 x} + c_2 e^{r_2 x}\]

\textbf{Case 2:} Repeated real root $r$:
\[y = (c_1 + c_2 x) e^{rx}\]

\textbf{Case 3:} Complex roots $r = \alpha \pm \beta i$:
\[y = e^{\alpha x}(c_1 \cos(\beta x) + c_2 \sin(\beta x))\]

\textbf{Method of undetermined coefficients:}
For $ay'' + by' + cy = f(x)$, guess particular solution based on $f(x)$:
\begin{align}
f(x) = e^{\alpha x} &\Rightarrow y_p = Ae^{\alpha x} \\
f(x) = \sin(\beta x) &\Rightarrow y_p = A\cos(\beta x) + B\sin(\beta x) \\
f(x) = x^n &\Rightarrow y_p = A_n x^n + A_{n-1} x^{n-1} + \cdots + A_0
\end{align}

\textbf{Laplace transform method:}
\[\mathcal{L}\{f(t)\} = F(s) = \int_0^\infty e^{-st} f(t) dt\]

\textbf{Key properties:}
\begin{align}
\mathcal{L}\{f'(t)\} &= sF(s) - f(0) \\
\mathcal{L}\{f''(t)\} &= s^2F(s) - sf(0) - f'(0)
\end{align}

