% Introduction to Topology
\section*{Topological Spaces and Continuity}

\begin{definition}
A \textbf{topology} on a set $X$ is a collection $\mathcal{T}$ of subsets of $X$ satisfying:
\begin{enumerate}
\item $\emptyset, X \in \mathcal{T}$
\item Any union of sets in $\mathcal{T}$ is in $\mathcal{T}$
\item Any finite intersection of sets in $\mathcal{T}$ is in $\mathcal{T}$
\end{enumerate}
The pair $(X, \mathcal{T})$ is called a \textbf{topological space}.
\end{definition}

\textbf{Examples of topologies:}
\begin{itemize}
\item \textbf{Discrete topology:} $\mathcal{T} = \mathcal{P}(X)$ (all subsets)
\item \textbf{Trivial topology:} $\mathcal{T} = \{\emptyset, X\}$
\item \textbf{Standard topology on $\mathbb{R}$:} Open sets are unions of open intervals
\end{itemize}

\begin{definition}
A function $f: X \to Y$ between topological spaces is \textbf{continuous} if for every open set $V \subseteq Y$, the preimage $f^{-1}(V)$ is open in $X$.
\end{definition}

\textbf{Metric spaces:}
A \textbf{metric} on a set $X$ is a function $d: X \times X \to \mathbb{R}$ satisfying:
\begin{enumerate}
\item $d(x,y) \geq 0$ with equality iff $x = y$
\item $d(x,y) = d(y,x)$ (symmetry)
\item $d(x,z) \leq d(x,y) + d(y,z)$ (triangle inequality)
\end{enumerate}

\textbf{Induced topology:}
The open ball $B_r(x) = \{y \in X : d(x,y) < r\}$ generates a topology where open sets are unions of open balls.

\textbf{Compactness:}
A topological space is \textbf{compact} if every open cover has a finite subcover.

\textbf{Heine-Borel theorem:}
In $\mathbb{R}^n$, a set is compact iff it is closed and bounded.

\textbf{Connectedness:}
A space $X$ is \textbf{connected} if it cannot be written as $X = A \cup B$ where $A, B$ are non-empty, disjoint, and both open (or both closed).

\textbf{Path connectedness:}
$X$ is \textbf{path-connected} if for any $x, y \in X$, there exists a continuous function $\gamma: [0,1] \to X$ with $\gamma(0) = x$ and $\gamma(1) = y$.

\textbf{Fundamental theorem:} Path-connected $\Rightarrow$ connected.

