\end{equation}

\section{Coordinate Systems And Geometry}

\subsection{Coordinate Transformations}

\begin{equation}
x = r\cos\theta, \quad y = r\sin\theta \quad \text{and} \quad r = \sqrt{x^2 + y^2}, \quad \theta = \arctan\frac{y}{x}
\end{equation}

\begin{equation}
x = \rho\sin\phi\cos\theta, \quad y = \rho\sin\phi\sin\theta, \quad z = \rho\cos\phi \quad \text{(spherical)}
\end{equation}

\begin{equation}
dA = r \, dr \, d\theta \quad \text{and} \quad dV = \rho^2 \sin\phi \, d\rho \, d\phi \, d\theta
\end{equation}

\subsection{Conic Sections}

\begin{equation}
\frac{x^2}{a^2} + \frac{y^2}{b^2} = 1 \quad \text{(ellipse)} \quad \text{and} \quad \frac{x^2}{a^2} - \frac{y^2}{b^2} = 1 \quad \text{(hyperbola)}
\end{equation}

\begin{equation}
y^2 = 4px \quad \text{(parabola)} \quad \text{with focus at} \quad (p, 0)
\end{equation}

\begin{equation}
\text{Distance formula: } d = \sqrt{(x_2-x_1)^2 + (y_2-y_1)^2}
\end{equation}

\section{Stress Testing}

\subsection{Syntax Stress Tests}

Complex inline: \(\sum_{k=1}^n \binom{n}{k} x^k (1-x)^{n-k} = 1\) within text.

Multiple inline in sequence: \(a_1\), \(a_2\), \ldots, \(a_n\) are coefficients.

