\begin{equation}
\sum_{n=1}^{\infty} \sum_{k=1}^{n} \frac{(-1)^{k+1}}{k} \binom{n}{k} = \sum_{n=1}^{\infty} \frac{1}{n} = \infty
\end{equation}

\begin{equation}
\int_0^{2\pi} \int_0^{\pi} \int_0^{1} r^2 \sin\phi \, dr \, d\phi \, d\theta = \frac{8\pi}{3}
